import { useRef, useState } from 'react';
import classes from './Form.module.css'

export default function FormPage() {
    const currentEntered = useRef();
    const yearlyEntered = useRef();
    const expectedEntered = useRef();
    const durationEntered = useRef();
  
    const [userInputs,setUserInputs] = useState({
      currentSavings: '',
      yearlyContribution: '',
      expectedReturn: '',
      duration: ''
    });
  
    function submitHandler(event) {
      event.preventDefault();
      setUserInputs({
        currentSavings: currentEntered.current.value,
        yearlyContribution: currentEntered.current.value,
        expectedReturn: currentEntered.current.value,
        duration: currentEntered.current.value
      });      


      console.log(userInputs);
    }


    return(
        <form className={classes.form} onSubmit={submitHandler}>
        <div className={classes.input_group}>
          <p>
            <label htmlFor="current-savings">Current Savings ($)</label>
            <input type="number" id="current-savings" ref={e} />
          </p>
          <p>
            <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
            <input type="number" id="yearly-contribution" />
          </p>
        </div>
        <div className={classes.input_group}>
          <p>
            <label htmlFor="expected-return">
              Expected Interest (%, per year)
            </label>
            <input type="number" id="expected-return" />
          </p>
          <p>
            <label htmlFor="duration">Investment Duration (years)</label>
            <input type="number" id="duration" />
          </p>
        </div>
        <p className={classes.actions}>
          <button type="reset" className={classes.buttonAlt}>
            Reset
          </button>
          <button className={classes.button}>
            Calculate
          </button>
        </p>
      </form>
    )
}
