import { useRef, useState } from 'react';
import classes from './Form.module.css'

export default function FormPage({onClac}) {
    const currentEntered = useRef();
    const yearlyEntered = useRef();
    const expectedEntered = useRef();
    const durationEntered = useRef();
  
    const [userInputs,setUserInputs] = useState({
      currentSavings: '',
      yearlyContribution: '',
      expectedReturn: '',
      duration: ''
    });
  
    function submitHandler(event) {
      event.preventDefault();
      setUserInputs({
        currentSavings: currentEntered.current.value,
        yearlyContribution: yearlyEntered.current.value,
        expectedReturn: expectedEntered.current.value,
        duration: durationEntered.current.value
      });      

      console.log('Submit');
      onClac(userInputs);
    }
    
    function resetHandler() {
      console.log('RESET');
    }

    function inputChangeHandler(input,value) {

    }

    return(
        <form className={classes.form} onSubmit={submitHandler}>
        <div className={classes.input_group}>
          <p>
            <label htmlFor="current-savings">Current Savings ($)</label>
            <input onChange={() => inputChangeHandler} type="number" id="current-savings" ref={currentEntered} />
          </p>
          <p>
            <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
            <input type="number" id="yearly-contribution" ref={yearlyEntered} />
          </p>
        </div>
        <div className={classes.input_group}>
          <p>
            <label htmlFor="expected-return">
              Expected Interest (%, per year)
            </label>
            <input type="number" id="expected-return" ref={expectedEntered} />
          </p>
          <p>
            <label htmlFor="duration">Investment Duration (years)</label>
            <input type="number" id="duration" ref={durationEntered} />
          </p>
        </div>
        <p className={classes.actions}>
          <button onClick={resetHandler} type='reset' className={classes.buttonAlt}>
            Reset
          </button>
          <button type='submit' className={classes.button}>
            Calculate
          </button>
        </p>
      </form>
    )
}
